import React, { useEffect, useState } from 'react';
import axios from 'axios';

interface BlockData {
  id: number;
  block_height: number;
  timestamp: string;
}

const BlockHeight: React.FC = () => {
  const [blockData, setBlockData] = useState<BlockData | null>(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:4000/api/block-height');
        setBlockData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();

    const interval = setInterval(fetchData, 60000); // Update every minute
    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <h1>Latest Block Data</h1>
      {blockData ? (
        <div>
          <p><strong>ID:</strong> {blockData.id}</p>
          <p><strong>Block Height:</strong> {blockData.block_height}</p>
          <p><strong>Timestamp:</strong> {new Date(blockData.timestamp).toLocaleString()}</p>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default BlockHeight;
