import React, { useEffect, useState } from 'react';
import axios from 'axios';

const BlockHeight: React.FC = () => {
  const [blockHeight, setBlockHeight] = useState<number | null>(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:4000/api/block-height');
        setBlockHeight(response.data.block_height);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();

    const interval = setInterval(fetchData, 60000); // Update every minute
    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <h1>Current Block Height</h1>
      {blockHeight !== null ? <p>{blockHeight}</p> : <p>Loading...</p>}
    </div>
  );
};

export default BlockHeight;
